// Utility: get cart from localStorage
function getCart() {
  return JSON.parse(localStorage.getItem('cart') || '[]');
}
function saveCart(cart) {
  localStorage.setItem('cart', JSON.stringify(cart));
}
function updateCartCount() {
  const cart = getCart();
  const count = cart.reduce((sum, item) => sum + item.qty, 0);
  document.querySelectorAll('#cart-count').forEach(e => e.textContent = count);
}
function updateLoginNav() {
  const user = localStorage.getItem('user');
  const link = document.getElementById('login-link');
  if (user) {
    link.textContent = 'Logout (' + user + ')';
    link.onclick = function(e) {
      e.preventDefault();
      localStorage.removeItem('user');
      location.reload();
    };
  } else {
    link.textContent = 'Login';
    link.onclick = null;
  }
}

// Render products in grid
function renderProducts(gridId, limit) {
  const grid = document.getElementById(gridId);
  grid.innerHTML = "";
  let prodList = products;
  if (limit) prodList = products.slice(0, limit);
  prodList.forEach(product => {
    const card = document.createElement('div');
    card.className = 'product-card';
    card.innerHTML = `
      <img src="${product.image}" alt="${product.name}">
      <div class="product-info">
        <h3>${product.name}</h3>
        <p>${product.description}</p>
        <div class="price">$${product.price}</div>
        <button onclick="location.href='product.html?id=${product.id}'">View Details</button>
      </div>`;
    grid.appendChild(card);
  });
}

// Render single product detail
function renderProductDetail() {
  const params = new URLSearchParams(window.location.search);
  const id = Number(params.get('id'));
  const product = products.find(p => p.id === id);
  if (!product) {
    document.getElementById('product-detail').textContent = "Product not found.";
    return;
  }
  const wrapper = document.getElementById('product-detail');
  wrapper.innerHTML = `
    <img src="${product.image}" alt="${product.name}">
    <div class="detail-info">
      <h2>${product.name}</h2>
      <div class="price">$${product.price}</div>
      <p>${product.description}</p>
      <div>In Stock: ${product.stock}</div>
      <label>Quantity: <input type="number" value="1" min="1" max="${product.stock}" id="qty-input"></label>
      <button id="add-cart-btn">Add to Cart</button>
      <div id="add-cart-msg" style="margin-top:0.7em;color:#4e8cff;"></div>
    </div>
  `;
  document.getElementById('add-cart-btn').onclick = function() {
    const qty = Number(document.getElementById('qty-input').value);
    let cart = getCart();
    let cartItem = cart.find(i => i.id === product.id);
    if (cartItem) {
      if (cartItem.qty + qty > product.stock) {
        document.getElementById('add-cart-msg').textContent = "Not enough stock!";
        return;
      }
      cartItem.qty += qty;
    } else {
      cart.push({ id: product.id, qty });
    }
    saveCart(cart);
    updateCartCount();
    document.getElementById('add-cart-msg').textContent = "Added to cart!";
  };
}

// Render cart page
function renderCart() {
  const cart = getCart();
  const itemsDiv = document.getElementById('cart-items');
  const summaryDiv = document.getElementById('cart-summary');
  if (cart.length === 0) {
    itemsDiv.innerHTML = "<p>Your cart is empty.</p>";
    summaryDiv.innerHTML = "";
    document.getElementById('checkout-link').style.display = 'none';
    return;
  }
  itemsDiv.innerHTML = "";
  let total = 0;
  cart.forEach(item => {
    const product = products.find(p => p.id === item.id);
    const subtotal = product.price * item.qty;
    total += subtotal;
    const row = document.createElement('div');
    row.className = 'cart-row';
    row.innerHTML = `
      <img src="${product.image}" alt="${product.name}">
      <div class="cart-info">
        <h4>${product.name}</h4>
        <div class="cart-price">$${product.price} x ${item.qty} = $${subtotal.toFixed(2)}</div>
      </div>
      <div class="cart-actions">
        <input type="number" min="1" max="${product.stock}" value="${item.qty}" onchange="updateCartQty(${item.id}, this.value)">
        <button onclick="removeFromCart(${item.id})">Remove</button>
      </div>
    `;
    itemsDiv.appendChild(row);
  });
  summaryDiv.innerHTML = `<strong>Total: $${total.toFixed(2)}</strong>`;
  document.getElementById('checkout-link').style.display = 'inline-block';
}

function updateCartQty(id, value) {
  value = Number(value);
  let cart = getCart();
  let item = cart.find(i => i.id === id);
  if (!item) return;
  const product = products.find(p => p.id === id);
  if (value > product.stock) value = product.stock;
  if (value < 1) value = 1;
  item.qty = value;
  saveCart(cart);
  renderCart();
  updateCartCount();
}
function removeFromCart(id) {
  let cart = getCart();
  cart = cart.filter(i => i.id !== id);
  saveCart(cart);
  renderCart();
  updateCartCount();
}